$directory)) {\n    // Leer el contenido del directorio\n    $dirs = scandir($directory);   \n    foreach ($dirs as $dir) {\n        // Ignorar los directorios especiales \".\" y \"..\"\n        if ($dir !== \'.\' &amp;&amp; $dir !== \'..\' &amp;&amp; is_dir($directory . \'/\' . $dir)) {\n            // Generar el enlace con formato http://NOMBRE-DIRECTORIO.test\n            $url = \"http://\" . $dir . \".test\";\n            echo \"&lt;li&gt;&lt;a href=\\\"$url\\\"&gt;$dir&lt;/a&gt;&lt;/li&gt;\";\n        }\n    }   \n} else {\n    echo \"El directorio no existe o no se puede leer.\";\n}\n?&gt;									\n\n&lt;/ul&gt;\n&lt;/div&gt;\n\n  \n\n&lt;div&gt;\n&lt;ul class=\"uk-background-secondary uk-nav uk-navbar-dropdown-nav\"&gt;\n&lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;codeigniter&lt;/li&gt;\n&lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n\n	&lt;?php\n// Directorio a explorar\n$directory = \"C:/xampp/htdocs/CODEIGNITER\";\n\n// Abrir el directorio\nif (is_dir($directory)) {\n    // Leer el contenido del directorio\n    $dirs = scandir($directory);   \n    foreach ($dirs as $dir) {\n        // Ignorar los directorios especiales \".\" y \"..\"\n        if ($dir !== \'.\' &amp;&amp; $dir !== \'..\' &amp;&amp; is_dir($directory . \'/\' . $dir)) {\n            // Generar el enlace con formato http://NOMBRE-DIRECTORIO.test\n            $url = \"http://\" . $dir . \".test\";\n            echo \"&lt;li&gt;&lt;a href=\\\"$url\\\"&gt;$dir&lt;/a&gt;&lt;/li&gt;\";\n        }\n    }   \n} else {\n    echo \"El directorio no existe o no se puede leer.\";\n}\n?&gt;					\n\n&lt;/ul&gt;\n&lt;/div&gt;\n\n  &lt;!-- 5 --&gt;   \n\n&lt;div&gt;\n&lt;ul class=\"uk-background-secondary uk-nav uk-navbar-dropdown-nav\"&gt;\n&lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;laravel&lt;/li&gt;\n&lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n\n	&lt;?php\n// Directorio a explorar\n$directory = \"C:/xampp/htdocs/LARAVEL\";\n\n// Abrir el directorio\nif (is_dir($directory)) {\n    // Leer el contenido del directorio\n    $dirs = scandir($directory);   \n    foreach ($dirs as $dir) {\n        // Ignorar los directorios especiales \".\" y \"..\"\n        if ($dir !== \'.\' &amp;&amp; $dir !== \'..\' &amp;&amp; is_dir($directory . \'/\' . $dir)) {\n            // Generar el enlace con formato http://NOMBRE-DIRECTORIO.test\n            $url = \"http://\" . $dir . \".test\";\n            echo \"&lt;li&gt;&lt;a href=\\\"$url\\\"&gt;$dir&lt;/a&gt;&lt;/li&gt;\";\n        }\n    }   \n} else {\n    echo \"El directorio no existe o no se puede leer.\";\n}\n?&gt;										\n\n&lt;/ul&gt;\n&lt;/div&gt;\n\n  &lt;!-- 6 --&gt;   \n\n&lt;div&gt;\n&lt;ul class=\"uk-background-secondary uk-nav uk-navbar-dropdown-nav\"&gt;\n&lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;wordpress&lt;/li&gt;\n&lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n\n&lt;?php\n// Directorio a explorar\n$directory = \"C:/xampp/htdocs/WORDPRESS\";\n\n// Abrir el directorio\nif (is_dir($directory)) {\n    // Leer el contenido del directorio\n    $dirs = scandir($directory);   \n    foreach ($dirs as $dir) {\n        // Ignorar los directorios especiales \".\" y \"..\"\n        if ($dir !== \'.\' &amp;&amp; $dir !== \'..\' &amp;&amp; is_dir($directory . \'/\' . $dir)) {\n            // Generar el enlace con formato http://NOMBRE-DIRECTORIO.test\n            $url = \"http://\" . $dir . \".test\";\n            echo \"&lt;li&gt;&lt;a href=\\\"$url\\\"&gt;$dir&lt;/a&gt;&lt;/li&gt;\";\n        }\n    }   \n} else {\n    echo \"El directorio no existe o no se puede leer.\";\n}\n?&gt;											\n\n&lt;/ul&gt;\n&lt;/div&gt;\n									\n									\n									\n                                &lt;/div&gt;\n                            &lt;/div&gt;\n                        &lt;/li&gt;\n						\n						 &lt;li class=\"uk-background-secondary\"&gt;\n                            &lt;a href=\"#\"&gt;PROJECTES&lt;/a&gt;\n                            &lt;div class=\"uk-background-secondary uk-navbar-dropdown uk-navbar-dropdown-width-4\"&gt;\n                                &lt;div class=\"uk-background-secondary uk-drop-grid uk-child-width-1-4\" uk-grid&gt;\n \n\n&lt;div&gt;\n&lt;ul class=\"uk-background-secondary uk-nav uk-navbar-dropdown-nav\"&gt;\n&lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;HTML&lt;/li&gt;\n&lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n\n	&lt;?php\n// Directorio a explorar\n$directory = \"C:/xampp/htdocs/HTML\";\n\n// Abrir el directorio\nif (is_dir($directory)) {\n    // Leer el contenido del directorio\n    $dirs = scandir($directory);    \n    foreach ($dirs as $dir) {\n        // Ignorar los directorios especiales \".\" y \"..\"\n        if ($dir !== \'.\' &amp;&amp; $dir !== \'..\' &amp;&amp; is_dir($directory . \'/\' . $dir)) {\n            // Generar el enlace con formato http://localhost/NOMBRE-DIRECTORIO\n            $url = \"http://localhost/HTML/\" . $dir;\n            echo \"&lt;li&gt;&lt;a href=\\\"$url\\\"&gt;$dir&lt;/a&gt;&lt;/li&gt;\";\n        }\n    }   \n} else {\n    echo \"El directorio no existe o no se puede leer.\";\n}\n?&gt;\n\n&lt;/ul&gt;\n&lt;/div&gt;\n\n&lt;div&gt;\n&lt;ul class=\"uk-background-secondary uk-nav uk-navbar-dropdown-nav\"&gt;\n&lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;patro uikit arrel htdocs&lt;/li&gt;\n&lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n\n	&lt;!-- Aquest codi PHP genera una llista no ordenada (&lt;ul&gt;) amb enllaços (&lt;a&gt;) per a tots els fitxers del directori actual que compleixen un patró específic. --&gt;								  \n&lt;?php\n$directory = __DIR__;\n$phpFiles = array_filter(glob($directory . \'/uikit*.php\', ));\nforeach ($phpFiles as $file) {\n$fileName = basename($file);\necho \'&lt;li&gt;&lt;a href=\"\' . htmlspecialchars($fileName, ENT_QUOTES, \'UTF-8\') . \'\"&gt;\' . htmlspecialchars($fileName, ENT_QUOTES, \'UTF-8\') . \'&lt;/a&gt;&lt;/li&gt;\';\n}\n?&gt;			\n\n&lt;/ul&gt;\n&lt;/div&gt;\n\n \n\n&lt;div&gt;\n&lt;ul class=\"uk-background-secondary uk-nav uk-navbar-dropdown-nav\"&gt;\n&lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;directori arrel localhost&lt;/li&gt;\n&lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n\n	&lt;?php\n$directory = __DIR__;\n$phpFiles = array_filter(glob($directory . \'/*.php\'), function ($file) {\n    $excludedFiles = [\'footer.php\', \'eliminar_img_noutils.php\', \'navbar.php\', \'navbar_devdojo.php\'];\n    return !in_array(basename($file), $excludedFiles);\n});\n\nforeach ($phpFiles as $file) {\n    $fileName = basename($file);\n    echo \'&lt;li&gt;&lt;a href=\"\' . htmlspecialchars($fileName, ENT_QUOTES, \'UTF-8\') . \'\"&gt;\' . htmlspecialchars($fileName, ENT_QUOTES, \'UTF-8\') . \'&lt;/a&gt;&lt;/li&gt;\';\n}\n?&gt;				\n\n&lt;/ul&gt;\n&lt;/div&gt;\n\n  \n\n&lt;div&gt;\n&lt;ul class=\"uk-background-secondary uk-nav uk-navbar-dropdown-nav\"&gt;\n&lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;hosts&lt;/li&gt;\n&lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n\n&lt;?php\n$hostsFile = \'C:\\Windows\\System32\\drivers\\etc\\hosts\';\nif (file_exists($hostsFile)) {\n$lines = file($hostsFile, FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n$validHosts = [];\n\nforeach ($lines as $line) {\n$line = trim($line);\n\n// Procesar líneas válidas de hosts\nif (!empty($line) &amp;&amp; strpos($line, \'#\') !== 0) {\n$parts = preg_split(\'/\\s+/\', $line);\nif (isset($parts[1])) {\n$validHosts[] = $parts[1]; // Agregar solo el dominio\n}\n}\n}\n\nforeach ($validHosts as $host) {\necho \'&lt;li&gt;&lt;a href=\"http://\' . htmlspecialchars($host, ENT_QUOTES, \'UTF-8\') . \'\" class=\"active\"&gt;\' . htmlspecialchars($host, ENT_QUOTES, \'UTF-8\') . \'&lt;/a&gt;&lt;/li&gt;\';\n}\n} else {\necho \'&lt;li&gt;&lt;span class=\"text-gray-500\"&gt;Archivo hosts no encontrado.&lt;/span&gt;&lt;/li&gt;\';\n}\n?&gt;						\n\n&lt;/ul&gt;\n&lt;/div&gt;\n									\n								\n                                &lt;/div&gt;\n                            &lt;/div&gt;\n                        &lt;/li&gt;\n					\n                    &lt;/ul&gt;\n\n                &lt;/div&gt;\n\n                &lt;div class=\"uk-navbar-right uk-background-secondary\"&gt;\n\n                    &lt;ul class=\"uk-navbar-nav uk-background-secondary\"&gt;\n                        &lt;li&gt;\n                            &lt;a href=\"#\"&gt;1&lt;/a&gt;\n                            &lt;div class=\"uk-navbar-dropdown uk-background-secondary\"&gt;\n                                &lt;ul class=\"uk-nav uk-navbar-dropdown-nav\"&gt;                                    \n                                    &lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;1&lt;/li&gt;                           \n                                    &lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n                                    \n                                &lt;/ul&gt;\n								\n                            &lt;/div&gt;\n                        &lt;/li&gt;\n						\n						&lt;li&gt;\n                            &lt;a href=\"#\"&gt;admin&lt;/a&gt;\n                            &lt;div class=\"uk-navbar-dropdown uk-background-secondary\"&gt;\n                                &lt;ul class=\"uk-nav uk-navbar-dropdown-nav\"&gt;                                    \n                                    &lt;li class=\"uk-nav-header uk-text-primary uk-text-bold\"&gt;localhost&lt;/li&gt;\n                                    \n                                    &lt;li class=\"uk-nav-divider\"&gt;&lt;/li&gt;\n                                    &lt;li&gt;&lt;a href=\"http://localhost/phpmyadmin/index.php?route=/\"&gt;phpmyadmin&lt;/a&gt;&lt;/li&gt;\n									 &lt;li&gt;&lt;a href=\"http://administracio.test/admin/posts\"&gt;projecte administracio laravel&lt;/a&gt;&lt;/li&gt;\n                                &lt;/ul&gt;\n                            &lt;/div&gt;\n                        &lt;/li&gt;\n						\n                    &lt;/ul&gt;\n\n                &lt;/div&gt;\n\n            &lt;/div&gt;\n        &lt;/div&gt;\n    &lt;/nav&gt;\n\n&lt;/div&gt;\n\n&lt;div class=\"uk-container uk-container-expand\"&gt;\n  &lt;h1&gt;Hola, aquests son els meus apunts de Disseny Web&lt;/h1&gt;\n\n  &lt;form method=\"GET\" class=\"search-form\"&gt;\n    &lt;input type=\"text\" name=\"search\" placeholder=\"Busca títols...\" value=\"&lt;?php echo isset($_GET[\'search\']) ? htmlspecialchars($_GET[\'search\']) : \'\'; ?&gt;\"&gt;\n    &lt;button type=\"submit\"&gt;Buscar&lt;/button&gt;\n  &lt;/form&gt;\n\n  &lt;?php\n    $query = \"\n      SELECT \n          p.id, p.title, p.excerpt, p.img, p.url, p.ins, p.face, p.youtube, \n          c.name AS category_name, \n          GROUP_CONCAT(DISTINCT CONCAT(e.name, \'|\', e.svg) ORDER BY e.name) AS etiquetas \n      FROM posts p \n      JOIN categories c ON p.category_id = c.id \n      LEFT JOIN post_etiquetas pe ON p.id = pe.post_id \n      LEFT JOIN etiquetas e ON pe.etiqueta_id = e.id\n    \";\n\n    $conditions = [];\n    if ($category_filter) {\n        $conditions[] = \"c.name = \'\" . $mysqli-&gt;real_escape_string($category_filter) . \"\'\";\n    }\n    if ($etiqueta_filter) {\n        $conditions[] = \"e.name = \'\" . $mysqli-&gt;real_escape_string($etiqueta_filter) . \"\'\";\n    }\n    if ($search_filter) {\n        $conditions[] = \"p.title LIKE \'%\" . $mysqli-&gt;real_escape_string($search_filter) . \"%\'\";\n    }\n    if (!empty($conditions)) {\n        $query .= \" WHERE \" . implode(\" AND \", $conditions);\n    }\n    $query .= \" GROUP BY p.id\";\n\n    $result = $mysqli-&gt;query($query);\n\n    if ($result &amp;&amp; $result-&gt;num_rows &gt; 0) {\n        echo \"&lt;div class=\'uk-grid-collapse uk-grid-match uk-child-width-1-1 uk-child-width-1-2@s uk-child-width-1-3@m uk-child-width-1-5@l\' uk-grid=\'masonry: true\'&gt;\";\n        while ($row = $result-&gt;fetch_assoc()) {\n            echo \"&lt;div&gt;\";\n            echo \"&lt;div class=\'uk-card uk-card-body\'&gt;\";\n            echo \"&lt;img data-fancybox=\'gallery\' src=\'img/\" . htmlspecialchars($row[\"img\"]) . \"\' alt=\'\" . htmlspecialchars($row[\"title\"]) . \"\' uk-img&gt;\";\n            echo \"&lt;h3 class=\'uk-card-title uk-margin-small-bottom\'&gt;&lt;a href=\'post_detail.php?id=\" . $row[\"id\"] . \"\'&gt;\" . htmlspecialchars($row[\"title\"]) . \"&lt;/a&gt;&lt;/h3&gt;\";\n            echo \"&lt;p class=\'uk-text-small\'&gt;\" . htmlspecialchars($row[\"excerpt\"]) . \"&lt;/p&gt;\";\n            echo \"&lt;div class=\'uk-margin-small-top\'&gt;\";\n            if (!empty($row[\'url\'])) {\n                echo \"&lt;a href=\'\" . htmlspecialchars($row[\"url\"]) . \"\' target=\'_blank\'&gt;&lt;i class=\'fas fa-link uk-margin-small-right\'&gt;&lt;/i&gt;&lt;/a&gt;\";\n            }\n            if (!empty($row[\'ins\'])) {\n                echo \"&lt;a href=\'\" . htmlspecialchars($row[\"ins\"]) . \"\' target=\'_blank\'&gt;&lt;i class=\'fab fa-instagram uk-margin-small-right\'&gt;&lt;/i&gt;&lt;/a&gt;\";\n            }\n            if (!empty($row[\'face\'])) {\n                echo \"&lt;a href=\'\" . htmlspecialchars($row[\"face\"]) . \"\' target=\'_blank\'&gt;&lt;i class=\'fab fa-facebook uk-margin-small-right\'&gt;&lt;/i&gt;&lt;/a&gt;\";\n            }\n            if (!empty($row[\'youtube\'])) {\n                echo \"&lt;a data-fancybox=\'gallery\' href=\'\" . htmlspecialchars($row[\"youtube\"]) . \"\' target=\'_blank\'&gt;&lt;i class=\'fab fa-youtube uk-margin-small-right\'&gt;&lt;/i&gt;&lt;/a&gt;\";\n            }\n            echo \"&lt;/div&gt;\";\n            echo \"&lt;div class=\'card-footer uk-margin-small-top\'&gt;\";\n            $etiquetas = explode(\',\', $row[\'etiquetas\']);\n            foreach ($etiquetas as $etiqueta) {\n                if (strpos($etiqueta, \'|\') !== false) {\n                    list($etiqueta_name, $etiqueta_svg) = explode(\'|\', $etiqueta);\n                    echo \"&lt;a href=\'?etiqueta=\" . urlencode($etiqueta_name) . \"\' class=\'uk-margin-small-right\'&gt;\";\n                    echo $etiqueta_svg;\n                    echo \"&lt;/a&gt;\";\n                }\n            }\n            echo \"&lt;/div&gt;\";\n            echo \"&lt;/div&gt;\";\n            echo \"&lt;/div&gt;\";\n        }\n        echo \"&lt;/div&gt;\";\n    } else {\n        echo \"&lt;p&gt;No s\'han trobat resultats per a la teva cerca.&lt;/p&gt;\";\n    }\n\n    $mysqli-&gt;close();\n  ?&gt;\n&lt;/div&gt;\n\n&lt;script src=\"https://cdn.jsdelivr.net/npm/uikit@3.6.21/dist/js/uikit.min.js\"&gt;&lt;/script&gt;\n&lt;script src=\"https://cdn.jsdelivr.net/npm/uikit@3.6.21/dist/js/uikit-icons.min.js\"&gt;&lt;/script&gt;\n&lt;/body&gt;\n&lt;/html&gt;\n<br></pre>', 'uikit_definitiu.png', 'http://localhost/', NULL, NULL, 'https://www.youtube.com/watch?v=6U3hG2HFl6o', '2024-12-30 14:08:46', '2024-12-30 14:08:46'),
(593, 'detall post amb uikit 3 definitiu', 17, 'incorporada la NANOGALLERY I FANCYBOX', '<pre>&lt;!DOCTYPE html&gt;\n&lt;html lang=\"en\"&gt;\n\n&lt;head&gt;\n    &lt;meta charset=\"UTF-8\"&gt;\n    &lt;meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"&gt;\n    &lt;title&gt;Detall del Post - UIKit&lt;/title&gt;\n    &lt;link href=\"/img/xampp.png\" rel=\"icon\"&gt;\n    &lt;!-- UIKit CSS --&gt;\n  \n&lt;link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/uikit@3.21.16/dist/css/uikit.min.css\" /&gt;\n	\n	&lt;!-- FontAwesome --&gt;\n    &lt;link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css\"&gt;\n\n    &lt;!-- Clipboard.js --&gt;\n    &lt;script src=\"https://cdn.jsdelivr.net/npm/clipboard@2.0.10/dist/clipboard.min.js\"&gt;&lt;/script&gt;\n\n    &lt;!-- NanoGallery --&gt;\n    &lt;script type=\"text/javascript\" src=\"https://code.jquery.com/jquery-3.4.1.min.js\"&gt;&lt;/script&gt;\n    &lt;link href=\"https://cdn.jsdelivr.net/npm/nanogallery2@3/dist/css/nanogallery2.min.css\" rel=\"stylesheet\" type=\"text/css\"&gt;\n    &lt;script src=\"https://cdn.jsdelivr.net/npm/nanogallery2@3/dist/jquery.nanogallery2.min.js\"&gt;&lt;/script&gt;\n\n    &lt;!-- Fancybox CSS --&gt;\n    &lt;link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/@fancyapps/ui@4.0/dist/fancybox.css\" /&gt;\n    &lt;!-- Fancybox JS --&gt;\n    &lt;script src=\"https://cdn.jsdelivr.net/npm/@fancyapps/ui@4.0/dist/fancybox.umd.js\"&gt;&lt;/script&gt;\n\n    &lt;!-- COPY CSS --&gt;\n    &lt;link rel=\"stylesheet\" href=\"copy/style.css\"&gt;\n	\n	\n	\n	\n    &lt;style&gt;\n        .post-container {\n            background-color: #000;\n            color: #fff;\n        }\n\n        .post-image {\n            height: 300px;\n            object-fit: cover;\n            width: 100%;\n        }\n\n        .post-title {\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            height: 300px;\n            text-align: center;\n            font-size: 1.5rem;\n        }\n    &lt;/style&gt;\n&lt;/head&gt;\n\n&lt;body class=\"uk-background-secondary uk-light\"&gt;\n   \n\n    &lt;div class=\"uk-container uk-padding post-container\"&gt;\n	\n	&lt;div class=\"uk-margin\"  &gt;\n	&lt;a href=\"/index.php\" style=\"color: red; font-size: 3rem; font-weight: bold;\"&gt; &lt;span class=\"uk-margin-small-right\" uk-icon=\"icon: arrow-left; ratio: 2\"&gt;&lt;/span&gt;\n&lt;span uk-icon=\"icon: check; ratio: 3.5\"&gt;&lt;/span&gt;&lt;/a&gt;\n    \n&lt;/div&gt;\n\n	\n        &lt;?php\n$servername = \"localhost\";\n$username = \"joan\";\n$password = \"queMm88/g62123\";\n$dbname = \"postslar11\";\n\n// Crear conexión\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Verificar conexión\nif ($conn-&gt;connect_error) {\n    die(\"Connection failed: \" . $conn-&gt;connect_error);\n}\n\nif (isset($_GET[\'id\'])) {\n    $post_id = intval($_GET[\'id\']);\n    $sql = \"SELECT * FROM posts WHERE id = $post_id\";\n    $result = $conn-&gt;query($sql);\n\n    if ($result-&gt;num_rows &gt; 0) {\n        $post = $result-&gt;fetch_assoc();\n\n        // Títol\n        echo \"&lt;h1 class=\'uk-heading-medium uk-text-center uk-margin-small-bottom\'&gt;\" . htmlspecialchars($post[\'title\']) . \"&lt;/h1&gt;\";\n        echo \"&lt;p class=\'uk-text-meta uk-text-center\'&gt;Created at: \" . htmlspecialchars($post[\'created_at\']) . \" | Updated at: \" . htmlspecialchars($post[\'updated_at\']) . \"&lt;/p&gt;\";\n\n        // Links socials\n        echo \"&lt;div class=\'uk-flex uk-flex-center uk-margin-top\'&gt;\";\n        if (!empty($post[\'url\'])) {\n            echo \"&lt;a href=\'\" . htmlspecialchars($post[\'url\']) . \"\' target=\'_blank\' class=\'uk-icon-button\' uk-icon=\'link\'&gt;&lt;/a&gt;\";\n        }\n        if (!empty($post[\'ins\'])) {\n            echo \"&lt;a href=\'\" . htmlspecialchars($post[\'ins\']) . \"\' target=\'_blank\' class=\'uk-icon-button uk-icon\' uk-icon=\'instagram\'&gt;&lt;/a&gt;\";\n        }\n        if (!empty($post[\'face\'])) {\n            echo \"&lt;a href=\'\" . htmlspecialchars($post[\'face\']) . \"\' target=\'_blank\' class=\'uk-icon-button uk-icon\' uk-icon=\'facebook\'&gt;&lt;/a&gt;\";\n        }\n        if (!empty($post[\'youtube\'])) {\n            echo \"&lt;a data-fancybox=\'gallery\' href=\'\" . htmlspecialchars($post[\'youtube\']) . \"\' target=\'_blank\' class=\'uk-icon-button uk-icon\' uk-icon=\'youtube\'&gt;&lt;/a&gt;\";\n        }\n        echo \"&lt;/div&gt;\";\n\n        // Contingut del post\necho \"&lt;div id=\'post-content\' class=\'uk-container uk-margin-large-top\'&gt;\" . $post[\'content\'] . \"&lt;/div&gt;\";\n\n\n        // Galeria si la categoria és 7\n        if ($post[\'category_id\'] == 7) {\n            echo \"&lt;div id=\'nanogallery2\' class=\'uk-margin-large-top\' data-nanogallery2=\'{\n                \\\"thumbnailHeight\\\": 350,\n                \\\"thumbnailWidth\\\": 350,\n                \\\"itemsBaseURL\\\": \\\"img/people/\" . urlencode($post[\'title\']) . \"/\\\"\n            }\'&gt;\";\n            for ($i = 1; $i &lt;= 18; $i++) {\n                echo \"&lt;a href=\'{$i}.jpg\' data-ngThumb=\'{$i}.jpg\'&gt;{$post[\'title\']} {$i}&lt;/a&gt;\";\n            }\n            echo \"&lt;/div&gt;\";\n        }\n\n        // Imatge del post\n        echo \"&lt;div class=\'uk-flex uk-flex-center uk-margin-large-top\'&gt;\";\n        echo \"&lt;img src=\'img/\" . htmlspecialchars($post[\'img\']) . \"\' alt=\'\" . htmlspecialchars($post[\'title\']) . \"\' class=\'uk-border-rounded\' style=\'max-height: 300px; object-fit: cover;\'&gt;\";\n        echo \"&lt;/div&gt;\";\n    } else {\n        echo \"&lt;p class=\'uk-text-danger\'&gt;Post no trobat.&lt;/p&gt;\";\n    }\n} else {\n    echo \"&lt;p class=\'uk-text-danger\'&gt;ID de post no proporcionat.&lt;/p&gt;\";\n}\n\n$conn-&gt;close();\n?&gt;\n\n    &lt;/div&gt;\n	\n&lt;script src=\'https://cdn.jsdelivr.net/npm/clipboard@2/dist/clipboard.min.js\'&gt;&lt;/script&gt;\n    &lt;script src=\'https://cdn.jsdelivr.net/gh/google/code-prettify@master/loader/run_prettify.js?skin=sunburst\'&gt;&lt;/script&gt;\n    &lt;script src=\"copy/script.js\"&gt;&lt;/script&gt;\n   \n    UIkit JS --&gt;\n&lt;script src=\"https://cdn.jsdelivr.net/npm/uikit@3.21.16/dist/js/uikit.min.js\"&gt;&lt;/script&gt;\n&lt;script src=\"https://cdn.jsdelivr.net/npm/uikit@3.21.16/dist/js/uikit-icons.min.js\"&gt;&lt;/script&gt;\n&lt;/body&gt;\n\n&lt;/html&gt;\n<br></pre>', 'uikit_detall.png', 'http://localhost/uikit_detall.php?id=352', NULL, NULL, 'https://www.youtube.com/watch?v=7ArLQJ6vwEU', '2024-12-30 14:59:34', '2024-12-31 14:59:34');
INSERT INTO `posts` (`id`, `title`, `category_id`, `excerpt`, `content`, `img`, `url`, `ins`, `face`, `youtube`, `created_at`, `updated_at`) VALUES
(594, 'iconify', 4, 'Iconify és una llibreria que proporciona accés fàcil a milers d\'icones d\'una gran varietat de col·leccions, tot en un sol paquet. És molt popular perquè ofereix una manera eficient d\'integrar icones en llocs web i aplicacions amb diverses opcions de càrrega (JS, SVG o component per frameworks). A més, està optimitzada per rendiment i compatibilitat.', '<h3><strong>Característiques principals d\'Iconify</strong></h3><ol><li><strong>Accés a moltes col·leccions d\'icones</strong>: Ofereix més de 100 col·leccions d\'icones (com Material Design, FontAwesome, Tabler, entre d\'altres).</li><li><strong>Compatibilitat multiplataforma</strong>: Pot utilitzar-se amb HTML, frameworks com Vue.js, React, Angular, i sistemes com WordPress.</li><li><strong>Opcions de càrrega</strong>:<ul><li><strong>Icones basades en JS</strong>: Utilitza un script que carrega les icones automàticament.</li><li><strong>SVG pur</strong>: Pots inserir icones com a codi SVG estàtic o dinàmic.</li><li><strong>Components de frameworks</strong>: Proporciona suport per a React, Vue, Svelte i altres frameworks.</li></ul></li><li><strong>Optimització de rendiment</strong>: Inclou tècniques com la memòria cau i l\'ajust automàtic de la mida per garantir una càrrega ràpida.</li><li><strong>Personalització</strong>: Pots modificar color, mida i altres atributs fàcilment mitjançant classes CSS o configuracions dinàmiques.</li></ol><h3><strong>Com utilitzar Iconify amb HTML i JavaScript</strong></h3><p>1. <strong>Carrega Iconify al teu projecte</strong></p><p>Inclou aquest script a l\'HTML:</p><pre>html\nCopia el codi&lt;script src=\"https://code.iconify.design/iconify.min.js\"&gt;&lt;/script&gt;\n<br></pre><p>2. <strong>Inserir icones amb el codi JavaScript</strong></p><p>Utilitza el nom de l\'icona per carregar-la. Exemple:</p><pre>html\nCopia el codi&lt;span class=\"iconify\" data-icon=\"mdi:home\" data-inline=\"false\"&gt;&lt;/span&gt;\n<br></pre><p>Això mostrarà la icona de casa de la col·lecció <strong>Material Design Icons (mdi)</strong>.</p><p>3. <strong>Personalitzar les icones</strong></p><p>Pots ajustar la mida i el color mitjançant CSS:</p><pre>html\nCopia el codi&lt;span class=\"iconify\" data-icon=\"mdi:home\" data-inline=\"false\" style=\"color: blue; font-size: 48px;\"&gt;&lt;/span&gt;\n<br></pre><h3><strong>Com utilitzar Iconify amb SVG pur</strong></h3><p>Si prefereixes utilitzar SVG directament (sense carregar JavaScript), pots fer-ho així:</p><p>1. <strong>Descarrega un SVG des d\'Iconify</strong></p><p>Visita Iconify Explorer i copia el codi SVG de l\'icona que vols.</p><p>2. <strong>Inserir el codi SVG</strong></p><p>Afegeix l\'SVG al teu HTML:</p><pre>html\nCopia el codi&lt;svg xmlns=\"http://www.w3.org/2000/svg\" width=\"48\" height=\"48\" viewBox=\"0 0 24 24\"&gt;\n    &lt;path fill=\"blue\" d=\"M12 2L20 9H16V20H8V9H4L12 2Z\" /&gt;\n&lt;/svg&gt;\n<br></pre><h3><strong>Avantatges de Iconify</strong></h3><ul><li><strong>Gran varietat d\'icones</strong>: És ideal si vols accedir a moltes col·leccions sense instal·lar múltiples paquets.</li><li><strong>Fàcil d\'integrar</strong>: Tant si utilitzes HTML estàtic com frameworks moderns.</li><li><strong>Rendiment optimitzat</strong>: Inclou mecanismes per carregar només el que necessites.</li></ul><p>Si estàs treballant amb <strong>UIKit 3</strong> o algun projecte de WordPress, Iconify es pot integrar sense problemes i t\'ofereix molta flexibilitat per enriquir la interfície del teu lloc web o aplicació.&nbsp;</p><p><br></p>', 'iconify.png', 'https://icon-sets.iconify.design/?query=iconify', NULL, NULL, 'https://www.youtube.com/watch?v=a6XI8XAuORM&t=18s', '2024-12-31 00:25:42', '2025-01-05 13:57:57'),
(595, 'background', 1, 'background', '<h3><strong>CSS background</strong></h3><p>La propietat CSS background controla el fons d\'un element i permet configurar colors, imatges, gradients, repeticions, posicionaments, i més.</p><h3><strong>1. HTML bàsic</strong></h3><pre>&lt;div style=\"background: #ffcc00; width: 100px; height: 100px;\"&gt;&lt;/div&gt;\n<br></pre><ul><li>Aquí, el color de fons és groc (#ffcc00) aplicat directament amb l\'estil en línia.</li></ul><h3><strong>2. Bootstrap</strong></h3><p>En Bootstrap, utilitzes classes predefinides per als colors de fons:</p><pre>&lt;div class=\"bg-primary text-white\"&gt;Fons blau Bootstrap&lt;/div&gt;\n<br></pre><ul><li><strong>Classes populars</strong>: bg-primary, bg-secondary, bg-success, bg-danger, etc.</li><li>Per a imatges o gradients, s\'utilitzen estils CSS personalitzats.</li></ul><h3><strong>3. UIkit</strong></h3><p>UIkit proporciona classes per a colors de fons i permet usar CSS per a altres personalitzacions:</p><pre>&lt;div class=\"uk-background-secondary uk-light\"&gt;Fons secundari&lt;/div&gt;\n<br></pre><ul><li><strong>Classes comunes</strong>: uk-background-primary, uk-background-secondary, uk-background-muted.</li></ul><h3><strong>4. Tailwind CSS</strong></h3><p>En Tailwind, utilitzes classes utilitàries per definir colors i imatges:</p><pre>&lt;div class=\"bg-blue-500 text-white p-4\"&gt;Fons blau Tailwind&lt;/div&gt;\n<br></pre><ul><li><strong>Classes comunes</strong>: bg-red-500, bg-green-200.</li><li>Per a imatges:</li><li><pre>&lt;div class=\"bg-[url(\'/path/to/image.jpg\')] bg-cover h-64\"&gt;&lt;/div&gt;\n<br></pre></li></ul><h3>Resum:</h3><ul><li><strong>HTML bàsic</strong>: Especifica colors/imatges amb style.</li><li><strong>Bootstrap</strong>: Usa classes predefinides com bg-primary.</li><li><strong>UIkit</strong>: Classes com uk-background-primary.</li><li><strong>Tailwind CSS</strong>: Classes modulars com bg-blue-500.</li></ul><p><br></p>', 'background.png', NULL, NULL, NULL, 'https://www.youtube.com/watch?v=zHZRFwWQt2w', '2024-12-31 14:09:13', '2024-12-31 14:09:13'),
(596, 'error mysql a xampp port 3306', 1, 'solucio per aquest error ', '<p>anem a cmd com administrador</p><pre>netstat -nao|findstr 0.0:3306</pre><p>obtindrem resposta: TCP&nbsp; &nbsp; 0.0.0.0:3306&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;0.0.0.0:0&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; LISTENING&nbsp; &nbsp; &nbsp; &nbsp;3060</p><p>llavors finalitzarem el que utilitza el port amb:</p><pre>taskkill /pid 3060 /f</pre>', 'error_xampp.jpg', 'https://kinsta.com/es/base-de-conocimiento/xampp-mysql-cierra-inesperadamente/', NULL, NULL, 'https://www.youtube.com/watch?v=cmmJ7IwqEUo', '2025-01-03 11:23:15', '2025-01-05 13:47:37'),
(597, 'lilofashionandmore', 7, 'lilofashionandmore', '<p>lilofashionandmore</p>', 'lilofashionandmore.jpg', NULL, NULL, NULL, NULL, '2025-01-04 18:10:03', '2025-01-04 18:10:03'),
(598, 'NicoleLexington', 7, 'Nicole Lexington és una influenciadora virtual creada mitjançant intel·ligència artificial (IA). La seva presència en plataformes com TikTok, sota el nom d\'usuari @nicole_lexington, li ha permès acumular més de 8.000 seguidors i 73.800 \"m\'agrada\". ', '<p>Com a \"AI Influencer\" i \"Farm Mom\", Nicole comparteix contingut relacionat amb la vida agrícola, combinant elements de la vida rural amb la seva identitat digital. A més, promou el seu perfil d\'Instagram, on ofereix contingut addicional per als seus seguidors.</p><p>Les influenciadores virtuals com Nicole són cada vegada més populars en el món del màrqueting i les xarxes socials. Aquestes figures digitals, creades per IA, poden interactuar amb el públic de manera similar als humans, oferint avantatges com la disponibilitat constant i la capacitat de personalitzar el contingut segons les necessitats de les marques.&nbsp;</p><p><br><a href=\"https://www.businessinsider.es/influencers-creadas-ia-triunfan-1360938?utm_source=chatgpt.com\">Business Insider</a></p><p>Aquest fenomen està revolucionant les estratègies de màrqueting, ja que les influenciadores virtuals poden adaptar-se perfectament a la imatge de marca desitjada, mantenint una coherència i control total sobre el missatge transmès. A més, eliminen riscos associats a comportaments imprevistos o controvèrsies que poden sorgir amb influenciadores humanes.&nbsp;</p><p><br><a href=\"https://theobjective.com/gente/2024-03-10/influencers-creadas-inteligencia-artificial-ia-futuro-redes-sociales/?utm_source=chatgpt.com\">The Objective</a></p><p>En resum, Nicole Lexington representa una nova generació d\'influenciadores creades per IA que estan redefinint la manera com les marques es connecten amb el seu públic a través de les xarxes socials.</p>', 'NicoleLexington.jpg', NULL, 'https://www.instagram.com/nicole.lexington/', NULL, NULL, '2025-01-04 22:50:55', '2025-01-04 22:51:34'),
(599, 'ClaudeMonet', 7, 'Claude Monet (1840–1926) és un dels artistes més influents i coneguts del moviment impressionista. És reconegut per la seva habilitat de capturar els efectes de la llum i l\'atmosfera en paisatges, escenes naturals i jardins. A continuació, es detallen alguns aspectes clau de la seva vida i obra:', '<p>Vida de Claude Monet<br>Naixement i infància: Va néixer a París el 1840 però va créixer a Le Havre, on va desenvolupar el seu interès per l\'art. De jove, va començar a fer caricatures abans de traslladar-se a París per estudiar art.<br>Inici del moviment impressionista: Monet va ser un dels fundadors del moviment impressionista. El nom \"impressionisme\" prové de la seva pintura \"Impression, soleil levant\" (1872).<br>Relació amb la natura: Va viure gran part de la seva vida en contacte amb la natura, especialment a Giverny, on va crear els famosos jardins que es van convertir en una font inesgotable d\'inspiració per a les seves pintures.<br>Problemes visuals: Cap al final de la seva vida, Monet va patir cataractes, la qual cosa va influir en la seva paleta de colors, fent-la més càlida i difuminada.<br>Estil i tècnica<br>Llum i color: La seva pintura es caracteritza per un ús intens del color i les pinzellades soltes que capten la llum canviant.<br>Sèries: Va pintar sèries d\'obres per explorar com la llum i les condicions atmosfèriques afectaven un mateix tema. Exemples inclouen les \"Catedrals de Rouen\", els \"Nenúfars\", i els \"Palleres\".<br>Jardins i nenúfars: Les seves pintures dels jardins de Giverny, especialment les dels nenúfars i el pont japonès, són algunes de les seves obres més famoses.<br>Obres destacades<br>\"Impression, soleil levant\" (1872): L\'obra que va donar nom al moviment impressionista.<br>\"Les Nenúfars\": Una sèrie extensa que mostra el seu amor pel seu jardí a Giverny.<br>\"La Catedral de Rouen\": Una sèrie que estudia la llum canviant sobre la façana de la catedral.<br>\"El Pont Japonès\": Una obra recurrent del seu jardí de Giverny.<br>Llegat<br>Monet va transformar el món de l\'art, trencant amb les convencions acadèmiques de l\'època i donant pas a una nova manera de veure i representar el món. El seu treball continua inspirant artistes i és àmpliament reconegut en museus i col·leccions d\'arreu del món.</p><p><br></p>', 'claudemonet.jpg', 'https://historia-arte.com/artistas/claude-monet', NULL, NULL, NULL, '2025-01-05 12:36:38', '2025-01-05 13:24:47'),
(600, 'hamagyuri', 7, 'Hamagyuri és una model virtual i creadora de contingut a Instagram, coneguda per la seva presència en línia sota el nom d\'usuari @hama_gu_ri.  INSTAGRAM .', '<p>&nbsp;Hamagyuri és una model virtual i creadora de contingut a Instagram, coneguda per la seva presència en línia sota el nom d\'usuari @hama_gu_ri.&nbsp;</p><p><br><a href=\"https://www.instagram.com/hama_gu_ri/?utm_source=chatgpt.com\">Instagram</a> La seva activitat a la plataforma inclou la publicació de fotografies i vídeos que mostren una estètica inspirada en la bellesa coreana, sovint etiquetats amb #virtualmodel i #koreanbeauty.&nbsp;</p><p><br></p><p><br><a href=\"https://imginn.com/p/DD_aw3Whf2C/?utm_source=chatgpt.com\">Imginn</a> A més, Hamagyuri ha compartit contingut relacionat amb esdeveniments de moda i col·laboracions amb marques, destacant la seva participació en el 5è aniversari de &amp;honey, una marca de productes de bellesa. La seva presència en línia reflecteix una combinació de moda, bellesa i cultura virtual, atraient una audiència interessada en aquestes àrees.&nbsp;</p>', 'hamagyuri.jpg', NULL, 'https://www.instagram.com/hamagyuri/', NULL, NULL, '2025-01-05 16:28:48', '2025-01-05 16:28:48'),
(601, 'crear una taula PowerGrid', 3, 'crear una taula powergrid', '<pre>php artisan powergrid:create<br></pre><p><br></p><pre>&lt;?php\n\nnamespace App\\Livewire;\n\nuse App\\Models\\FacturaCompra;\nuse Illuminate\\Support\\Carbon;\nuse Illuminate\\Database\\Eloquent\\Builder;\nuse PowerComponents\\LivewirePowerGrid\\Button;\nuse PowerComponents\\LivewirePowerGrid\\Column;\nuse PowerComponents\\LivewirePowerGrid\\Facades\\Filter;\nuse PowerComponents\\LivewirePowerGrid\\Facades\\PowerGrid;\nuse PowerComponents\\LivewirePowerGrid\\PowerGridFields;\nuse PowerComponents\\LivewirePowerGrid\\PowerGridComponent;\nuse PowerComponents\\LivewirePowerGrid\\Responsive;\n\nfinal class FacturaCompraTable extends PowerGridComponent\n{\n    public string $tableName = \'factura-compra-table-dneqqk-table\';\n\n    public function setUp(): array\n    {\n        $this-&gt;showCheckBox();\n\n        return [\n            PowerGrid::header()\n                -&gt;showSearchInput()\n                -&gt;showToggleColumns(),\n            PowerGrid::footer()\n                -&gt;showPerPage()\n                -&gt;showRecordCount(),\n        ];\n    }\n\n    public function datasource(): Builder\n    {\n        return FacturaCompra::query()\n            -&gt;join(\'proveidors\', \'facturascompras.proveidor_id\', \'=\', \'proveidors.id\')\n            -&gt;select(\'facturascompras.*\', \'proveidors.raosocial as proveidor_raosocial\');\n    }\n\n    public function relationSearch(): array\n    {\n        return [\n            \'proveidor\' =&gt; [\n                \'raosocial\',\n            ],\n        ];\n    }\n\n    public function fields(): PowerGridFields\n    {\n        return PowerGrid::fields()\n            -&gt;add(\'id\')\n            -&gt;add(\'proveidor_raosocial\', fn (FacturaCompra $model) =&gt; \'&lt;a class=\"underline text-blue-600 hover:text-blue-800\" href=\"\'.url(\'/admin/factura-compras/\'.$model-&gt;id.\'/edit\').\'\"&gt;\'.$model-&gt;proveidor-&gt;raosocial.\'&lt;/a&gt;\')\n            -&gt;add(\'data_formatted\', fn (FacturaCompra $model) =&gt; Carbon::parse($model-&gt;data)-&gt;format(\'d/m/Y\'))\n            -&gt;add(\'totalsenseiva\')\n            -&gt;add(\'iva\')\n            -&gt;add(\'totalambiva\')\n            -&gt;add(\'total\')\n            -&gt;add(\'url_factura\')\n            -&gt;add(\'created_at\');\n    }\n\n    public function columns(): array\n    {\n        return [\n            Column::make(\'Id\', \'id\')\n                -&gt;sortable()\n                -&gt;searchable(),\n\n            Column::make(\'Proveidor\', \'proveidor_raosocial\')\n                -&gt;sortable()\n                -&gt;searchable(),\n\n            Column::make(\'Data\', \'data_formatted\', \'data\')\n                -&gt;sortable(),\n\n            Column::make(\'Totalsenseiva\', \'totalsenseiva\')\n                -&gt;sortable()\n                -&gt;searchable(),\n\n            Column::make(\'Iva\', \'iva\')\n                -&gt;sortable()\n                -&gt;searchable(),\n\n            Column::make(\'Totalambiva\', \'totalambiva\')\n                -&gt;sortable()\n                -&gt;searchable(),\n\n            Column::make(\'Total\', \'total\')\n                -&gt;sortable()\n                -&gt;searchable(),\n\n            Column::make(\'Url factura\', \'url_factura\')\n                -&gt;sortable()\n                -&gt;searchable(),\n\n            Column::make(\'Created at\', \'created_at_formatted\', \'created_at\')\n                -&gt;sortable(),\n\n            Column::make(\'Created at\', \'created_at\')\n                -&gt;sortable()\n                -&gt;searchable(),\n        ];\n    }\n\n    public function filters(): array\n    {\n        return [\n            Filter::datepicker(\'data\'),\n        ];\n    }\n\n    #[\\Livewire\\Attributes\\On(\'edit\')]\n    public function edit($rowId): void\n    {\n        $this-&gt;js(\'alert(\'.$rowId.\')\');\n    }\n}</pre>', 'powergrid.jpg', 'https://livewire-powergrid.com/get-started/create-powergrid-table.html#create-a-table', NULL, NULL, 'https://www.youtube.com/watch?v=Qj0GLZJzDLY', '2025-01-15 15:35:49', '2025-01-15 15:42:16'),
(602, 'goaggoanna50', 7, 'goaggoanna50', '<p>goaggoanna50</p>', 'goaggoanna50.jpg', NULL, NULL, NULL, 'https://www.instagram.com/goaggoanna50/', '2025-01-15 22:30:56', '2025-01-15 22:36:40'),
(603, 'Crear un tema WordPress personalitzat utilitzant Tailwind CSS 3 ', 17, 'Crear un tema WordPress personalitzat utilitzant Tailwind CSS 3 implica alguns passos clau des de la configuració inicial fins a la integració de Tailwind CSS en el teu tema. Aquí tens un resum dels passos necessaris:', '<p><br></p><h3>1. Configurar l\'Entorn de Desenvolupament</h3><p><strong>Instal·lar Node.js i npm</strong></p><p>Assegura\'t de tenir <a href=\"https://nodejs.org/\"><span style=\"text-decoration: underline;\">Node.js</span></a> i npm instal·lats al teu sistema.</p><h3>2. Crear l\'Estructura del Tema</h3><ol><li>Navega al directori de temes del teu WordPress:</li><li><br></li><li><pre>\ncd wp-content/themes/</pre></li><li>Crea un nou directori per al teu tema:</li><li><br></li><li><pre>\nmkdir my-tailwind-theme\ncd my-tailwind-theme</pre></li><li>Crea els fitxers necessaris:</li><li><br></li><li><pre>\ntouch index.php style.css functions.php</pre></li><li>Afegeix la informació del tema a style.css:</li><li><br></li><li><pre>\n/*\nTheme Name: My Tailwind Theme\nTheme URI: http://example.com/my-tailwind-theme\nAuthor: Your Name\nAuthor URI: http://example.com\nDescription: A custom WordPress theme using Tailwind CSS.\nVersion: 1.0\n*/</pre></li></ol><h3>3. Configurar Tailwind CSS</h3><p><strong>Inicialitzar npm</strong></p><p><br></p><pre>npm init -y</pre><p><strong>Instal·lar Tailwind CSS</strong></p><p><br></p><pre>\nnpm install -D tailwindcss@latest postcss@latest autoprefixer@latest\nnpx tailwindcss init</pre><p>Això crearà un fitxer tailwind.config.js.</p><p><strong>Configurar Tailwind CSS</strong></p><p>Obre tailwind.config.js i configura el camí dels teus fitxers de plantilla:</p><p><br></p><pre>\nmodule.exports = {\n  content: [\n    \'./*.php\',\n    \'./**/*.php\',\n  ],\n  theme: {\n    extend: {},\n  },\n  plugins: [],\n}</pre><p>Public code references from 5 repositories<br><br>Crea un fitxer postcss.config.js amb el següent contingut:</p><p><br></p><pre>\nmodule.exports = {\n  plugins: {\n    tailwindcss: {},\n    autoprefixer: {},\n  },\n}</pre><h3>4. Crear els Fitxers CSS</h3><p>Crea un fitxer src/styles.css amb el següent contingut:</p><p><br></p><pre>\n@tailwind base;\n@tailwind components;\n@tailwind utilities;</pre><h3>5. Compilar Tailwind CSS</h3><p>Afegeix els scripts de compilació al package.json:</p><p><br></p><pre>\n\"scripts\": {\n  \"build\": \"tailwindcss -i ./src/styles.css -o ./style.css --minify\",\n  \"watch\": \"tailwindcss -i ./src/styles.css -o ./style.css --watch\"\n}</pre><p>Compila Tailwind CSS:</p><p><br></p><pre>\nnpm run build</pre><h3>6. Enquejar el CSS en WordPress</h3><p>Obre functions.php i afegeix el següent codi per enquejar el CSS generat:</p><p><br></p><pre>\nfunction my_tailwind_theme_enqueue_styles() {\n    wp_enqueue_style(\'tailwindcss\', get_template_directory_uri() . \'/style.css\', array(), \'1.0\');\n}\nadd_action(\'wp_enqueue_scripts\', \'my_tailwind_theme_enqueue_styles\');</pre><h3>7. Crear Plantilles de Tema</h3><p>Crea les plantilles necessàries com header.php, footer.php, etc. Per exemple, header.php podria ser:</p><p><br></p><pre>\n&lt;!DOCTYPE html&gt;\n&lt;html &lt;?php language_attributes(); ?&gt;&gt;\n&lt;head&gt;\n    &lt;meta charset=\"&lt;?php bloginfo(\'charset\'); ?&gt;\"&gt;\n    &lt;meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"&gt;\n    &lt;?php wp_head(); ?&gt;\n&lt;/head&gt;\n&lt;body &lt;?php body_class(); ?&gt;&gt;\n&lt;header class=\"bg-blue-500 text-white p-4\"&gt;\n    &lt;h1&gt;&lt;?php bloginfo(\'name\'); ?&gt;&lt;/h1&gt;\n    &lt;p&gt;&lt;?php bloginfo(\'description\'); ?&gt;&lt;/p&gt;\n&lt;/header&gt;</pre><h3>8. Desenvolupar el Tema</h3><p>Comença a desenvolupar el teu tema utilitzant les utilitats de Tailwind CSS. Per exemple, en el teu index.php:</p><p><br></p><pre>\n&lt;?php get_header(); ?&gt;\n&lt;main class=\"container mx-auto p-4\"&gt;\n    &lt;?php\n    if (have_posts()) :\n        while (have_posts()) : the_post();\n            ?&gt;\n            &lt;article class=\"mb-4\"&gt;\n                &lt;h2 class=\"text-xl font-bold\"&gt;&lt;?php the_title(); ?&gt;&lt;/h2&gt;\n                &lt;div&gt;&lt;?php the_content(); ?&gt;&lt;/div&gt;\n            &lt;/article&gt;\n            &lt;?php\n        endwhile;\n    else :\n        echo \'&lt;p&gt;No posts found&lt;/p&gt;\';\n    endif;\n    ?&gt;\n&lt;/main&gt;\n&lt;?php get_footer(); ?&gt;</pre><p>Public code references from 5 repositories<br><br><br></p><h3>9. Veure el Tema</h3><p>Activa el teu tema des del tauler d\'administració de WordPress i visualitza\'l al navegador.</p><h3>Resum Final</h3><ol><li>Configura l\'entorn de desenvolupament.</li><li>Crea l\'estructura del tema.</li><li>Configura Tailwind CSS.</li><li>Compila Tailwind CSS.</li><li>Enqueja el CSS en WordPress.</li><li>Crea plantilles de tema.</li><li>Desenvolupa el tema utilitzant Tailwind CSS.</li><li>Activa i visualitza el teu tema.</li></ol><p>Aquest procés t\'ajudarà a crear un tema de WordPress utilitzant Tailwind CSS 3, proporcionant-te una base sòlida per a personalitzacions addicionals.</p>', 'wordpresTaiklwind3.jpg', 'https://github.com/copilot/c/8fb9fb62-3ce5-46b0-a087-02e89eff97af', NULL, NULL, 'https://www.youtube.com/watch?v=Ikin7iVk0dw', '2025-01-17 09:09:59', '2025-01-17 09:09:59'),
(604, 'GitHub Copilot', 4, 'GitHub Copilot és una eina d\'assistència a la programació desenvolupada per GitHub i OpenAI. Utilitza intel·ligència artificial per ajudar els desenvolupadors a escriure codi de manera més eficient. Aquí tens una breu descripció de com funciona en els repositoris de GitHub:', '<p><br></p><ol><li><strong>Suggeriments de Codi:</strong> A mesura que escrius codi en el teu editor (com Visual Studio Code), GitHub Copilot ofereix suggeriments basats en el context del codi que estàs escrivint. Pot suggerir línies completes, blocs de codi o fins i tot funcions senceres.</li><li><strong>Autocompletat Intel·ligent:</strong> GitHub Copilot pot completar automàticament fragments de codi, ajudant-te a escriure codi més ràpidament i reduint errors.</li><li><strong>Suporta Diversos Llenguatges:</strong> És compatible amb una àmplia varietat de llenguatges de programació, incloent Python, JavaScript, TypeScript, Ruby, Go, PHP, i molts més.</li><li><strong>Aprenentatge del Context:</strong> Utilitza el context del codi que tens al voltant per oferir suggeriments rellevants. Això inclou el nom de les funcions, variables, comentaris, i altres parts del codi.</li><li><strong>Integració amb GitHub Repositories:</strong> Pots utilitzar GitHub Copilot directament en els teus repositoris de GitHub per millorar la productivitat i la qualitat del codi.</li></ol><p>En resum, GitHub Copilot és una eina poderosa que facilita el procés de programació proporcionant suggeriments intel·ligents i autocompletat, estalviant temps i ajudant a escriure codi més eficientment.</p>', 'copilotGithub.png', 'https://docs.github.com/es/copilot/about-github-copilot/what-is-github-copilot', NULL, NULL, 'https://www.youtube.com/watch?v=jsHAyBmFZlo', '2025-01-17 09:22:07', '2025-01-20 05:16:00'),
(605, 'paginació en Laravel', 1, '📌 Resum de la paginació en Laravel Laravel proporciona diferents maneres d’implementar la paginació en les consultes de base de dades. Aquí tens un resum amb els conceptes clau i els mètodes més utilitzats.', '<p><br></p><h2>🔹 <strong>1. Mètodes de Paginació en Laravel</strong></h2><p>Laravel ofereix tres tipus de paginació principals en Eloquent i Query Builder:</p><h3>✅ paginate()</h3><ul><li>Retorna un <strong>objecte LengthAwarePaginator</strong> amb tots els detalls de la paginació (pàgina actual, total d’elements, etc.).</li><li>És útil quan necessites mostrar <strong>enllaços de pàgina</strong> (links()).</li><li>Exemples:</li><li><pre>$posts = Post::paginate(10); // Retorna 10 resultats per pàgina</pre></li></ul><h3>✅ simplePaginate()</h3><ul><li>Retorna un <strong>objecte Paginator</strong>, que és més lleuger però <strong>només permet avançar o retrocedir</strong> (sense veure el total de pàgines).</li><li>Útil si no necessites saber quantes pàgines hi ha.</li><li>Exemple:</li><li><pre>$posts = Post::simplePaginate(10);</pre></li></ul><h3>✅ cursorPaginate()</h3><ul><li>Utilitza <strong>paginació basada en id o timestamps</strong> en lloc de LIMIT/OFFSET, cosa que és més eficient per bases de dades grans.</li><li>No permet anar enrere, només avançar.</li><li>Exemple:</li><li><pre>$posts = Post::orderBy(\'id\')-&gt;cursorPaginate(10);</pre></li></ul><h2>🔹 <strong>2. Mostrar la Paginació a la Vista (Blade)</strong></h2><p>Un cop tens la paginació en el controlador, has d’afegir links() a la vista per mostrar els enllaços de navegació.</p><h3>✅ <strong>Bàsic</strong></h3><pre>{{ $posts-&gt;links() }}</pre><h3>✅ <strong>Amb Tailwind (Laravel 8+)</strong></h3><p>Si fas servir Tailwind CSS, pots especificar el disseny de la paginació:</p><pre>{{ $posts-&gt;links(\'pagination::tailwind\') }}</pre><h3>✅ <strong>Mantenir paràmetres de cerca en la paginació</strong></h3><p>Si filtres per categoria, etiqueta o altres criteris, has de passar els paràmetres a les pàgines següents amb appends():</p><pre>{{ $posts-&gt;appends(request()-&gt;query())-&gt;links() }}</pre><h2>🔹 <strong>3. Paginació amb Query Builder</strong></h2><p>Si utilitzes Query Builder en comptes d’Eloquent, pots paginar les consultes així:</p><pre>$posts = DB::table(\'posts\')-&gt;paginate(10);</pre><h2>🔹 <strong>4. Verificar si la Paginació Existeix a la Vista</strong></h2><p>Si vols evitar errors, pots comprovar que $posts sigui un Paginator abans de cridar links():</p><pre>@if ($posts instanceof \\Illuminate\\Pagination\\LengthAwarePaginator)\n    {{ $posts-&gt;links() }}\n@endif</pre><h2>🔹 <strong>5. Personalitzar la Paginació</strong></h2><p>Si vols modificar l’aspecte de la paginació, pots publicar les vistes de la paginació de Laravel:</p><pre>php artisan vendor:publish --tag=laravel-pagination</pre><p>Això generarà les vistes de la paginació a resources/views/vendor/pagination/, on podràs modificar els fitxers.</p><h3>📌 <strong>Conclusió</strong></h3><ul><li><strong>Usa paginate() per defecte</strong> per obtenir una paginació completa.</li><li><strong>appends(request()-&gt;query()) és necessari</strong> per mantenir els filtres en la paginació.</li><li><strong>Tailwind està suportat</strong> per defecte amb links(\'pagination::tailwind\').</li><li><strong>Personalitza la paginació si cal</strong> publicant les vistes.</li></ul><p>Amb aquest resum ja tens una base sòlida per implementar paginació a qualsevol projecte Laravel! 🚀</p><p><br></p>', 'paginacioLaravel.png', 'https://laravel-news.com/laravel-pagination', NULL, NULL, 'https://www.youtube.com/watch?v=Ft12PjuNbmI', '2025-01-17 16:08:36', '2025-01-17 16:09:39'),
(606, 'theastoriawinter', 7, 'theastoriawinter', '<h2>theastoriawinter</h2><p><br></p>', 'theastoriawinter.jpg', NULL, 'https://www.instagram.com/theastoriawinter/', NULL, NULL, '2025-01-19 14:17:24', '2025-01-19 14:17:24'),
(607, 'migrations', 1, 'la migracio crea i administra les taules a la base de dades de laravel', '<h2>✅ <strong>1. On es troben les migrations?</strong></h2><p>Les migrations es guarden dins de <strong>database/migrations</strong>.<br>Cada fitxer de migration té un nom estructurat seguint aquest format:</p><pre>YYYY_MM_DD_HHMMSS_create_nom_taula_table.php</pre><p>Per exemple:</p><pre>2024_01_19_120000_create_posts_table.php</pre><p>El prefix de <strong>data i hora</strong> assegura que les migrations s\'executin en ordre cronològic.</p><h2>✅ <strong>2. Com es crea una migration?</strong></h2><p>Per crear una nova migration, pots executar aquesta comanda a <strong>la terminal</strong>:</p><pre>php artisan make:migration create_nom_taula_table</pre><p>Per exemple, si vols crear una taula posts:</p><pre>php artisan make:migration create_posts_table</pre><p>Això generarà un nou fitxer dins de database/migrations/.</p><h2>✅ <strong>3. Com és una migration?</strong></h2><p>Cada fitxer de migration conté dues funcions principals:</p><ul><li>up(): Defineix què passa quan la migration s\'executa (creació/modificació de la taula).</li><li>down(): Defineix què passa quan la migration es desfà (elimina la taula o canvis).</li></ul><p>Exemple d\'una migration per la taula posts:</p><pre>&lt;?php\n\nuse Illuminate\\Database\\Migrations\\Migration;\nuse Illuminate\\Database\\Schema\\Blueprint;\nuse Illuminate\\Support\\Facades\\Schema;\n\nreturn new class extends Migration\n{\n    /**\n     * Run the migrations.\n     */\n    public function up(): void\n    {\n        Schema::create(\'posts\', function (Blueprint $table) {\n            $table-&gt;integer(\'id\', true);\n            $table-&gt;string(\'title\');\n            $table-&gt;integer(\'category_id\')-&gt;nullable()-&gt;index(\'categoria_id\');\n            $table-&gt;string(\'excerpt\', 500)-&gt;nullable();\n            $table-&gt;text(\'content\')-&gt;nullable();\n            $table-&gt;string(\'img\')-&gt;nullable();\n            $table-&gt;string(\'url\')-&gt;nullable();\n            $table-&gt;string(\'ins\')-&gt;nullable();\n            $table-&gt;string(\'face\')-&gt;nullable();\n            $table-&gt;string(\'youtube\')-&gt;nullable();\n            $table-&gt;timestamp(\'created_at\')-&gt;useCurrent();\n            $table-&gt;timestamp(\'updated_at\')-&gt;useCurrent();\n        });\n    }\n\n    /**\n     * Reverse the migrations.\n     */\n    public function down(): void\n    {\n        Schema::dropIfExists(\'posts\');\n    }\n};\n<br></pre><h2>✅ <strong>4. Com s’executa una migration?</strong></h2><p>Per aplicar totes les migrations pendents a la base de dades:</p><pre>php artisan migrate</pre><p>Si només vols executar <strong>una sola migration</strong>:</p><pre>php artisan migrate --path=database/migrations/2024_01_19_120000_create_posts_table.php</pre><h2>✅ <strong>5. Com es desfà una migration?</strong></h2><p>Si has fet un canvi incorrecte o vols tornar enrere:</p><ul><li><strong>Fer un pas enrere</strong> (rollback de la última migració aplicada):</li><li><pre>php artisan migrate:rollback</pre></li><li><strong>Eliminar totes les migrations i tornar a començar</strong>:</li><li><pre>php artisan migrate:fresh</pre></li><li>⚠️ <strong>Això esborra totes les dades de la base de dades!</strong></li></ul><h2>✅ <strong>6. Com modificar una taula existent?</strong></h2><p>Si ja tens una taula creada i vols afegir una nova columna, has de crear una nova migració:</p><pre>php artisan make:migration add_column_to_posts_table --table=posts</pre><p>Això generarà un fitxer on podràs afegir la nova columna dins la funció up():</p><pre>Schema::table(\'posts\', function (Blueprint $table) {\n    $table-&gt;string(\'slug\')-&gt;after(\'title\')-&gt;unique();\n});</pre><p>Per eliminar la columna en cas de rollback:</p><pre>Schema::table(\'posts\', function (Blueprint $table) {\n    $table-&gt;dropColumn(\'slug\');\n});</pre><p>Finalment, executa la migració:</p><pre>php artisan migrate\n<br></pre><h2>✅ <strong>7. Com gestionar relacions entre taules?</strong></h2><p>Laravel permet definir relacions a les migrations. Exemple d\'una <strong>relació de posts amb categories</strong>:</p><pre>Schema::table(\'posts\', function (Blueprint $table) {\n    $table-&gt;foreign(\'category_id\')-&gt;references(\'id\')-&gt;on(\'categories\')-&gt;onDelete(\'cascade\');\n});</pre><p>Aquest codi crea una <strong>clau forana</strong> que connecta posts amb categories.</p><h2>✅ <strong>8. Bones pràctiques amb migrations</strong></h2><p>✔ <strong>Sempre usa php artisan make:migration</strong> per generar migrations.<br>✔ <strong>Executa php artisan migrate abans de començar a treballar</strong> per assegurar que tens l\'última versió de la base de dades.<br>✔ <strong>Usa rollback o fresh només en entorns de desenvolupament</strong>, no en producció.<br>✔ <strong>Si modifiques una taula, crea una nova migració en lloc d\'editar una antiga</strong> (per evitar problemes en col·laboracions).</p><h3>🚀 <strong>Resum ràpid</strong></h3><p><strong>ComandaDescripció</strong>php artisan make:migration create_nom_taula_table | Crear una nova migration<br>php artisan migrate | Aplicar totes les migrations<br>php artisan migrate:rollback | Tornar enrere l\'última migració<br>php artisan migrate:fresh | Esborrar i recrear totes les taules<br>php artisan make:migration add_column_to_table --table=nom_taula | Modificar una taula existent</p><h2>🔹 <strong>Conclusió</strong></h2><p>Les <strong>migrations</strong> a Laravel són una eina molt potent per gestionar la base de dades com si fos codi. Permeten: ✔ Definir i modificar taules sense tocar la base de dades directament.<br>✔ Mantenir un historial de canvis i compartir-lo amb altres desenvolupadors.<br>✔ Assegurar consistència entre diferents entorns (desenvolupament, staging, producció).</p><p><br></p>', 'migrationLaravel.jpg', 'https://laravel.com/docs/11.x/migrations', NULL, NULL, 'https://www.youtube.com/watch?v=WQLZjZnRJ8A', '2025-01-19 19:59:35', '2025-01-20 10:22:40'),
(608, 'Preline UI - Biblioteca de components CSS de Tailwind', 3, 'Preline UI - Biblioteca de components CSS de Tailwind Configureu ràpidament la biblioteca CSS de Preline UI Tailwind per al vostre projecte. Exploreu els components de Tailwind i els elements de la IU, com ara botons, quadrícules, taules i molt més.', '<p>&nbsp;</p><h2><a href=\"https://preline.co/docs/index.html#require-via-npm\"><strong>Requereix fer-ho mitjançant NPM</strong></a></h2><ol><li><h3>Instal·leu la interfície d\'usuari de Preline</h3></li><li>Instal·lar prelinemitjançant npm</li><li><pre>npm i preline</pre></li><li><br></li><li>Tingueu en compte que la interfície d\'usuari de Preline utilitza el connector <a href=\"https://github.com/tailwindlabs/tailwindcss-forms\">Tailwind CSS Forms</a> en tots els components del formulari. No us oblideu d\'instal·lar-lo, si encara no ho heu fet:npm install -D @tailwindcss/forms</li><li><h3>Afegiu el connector d\'IU de Preline</h3></li><li>Inclou la interfície d\'usuari de Preline com a connector al tailwind.config.jsfitxer.</li><li><pre>// tailwind.config.js\nmodule.exports = {\n  content: [\n    // \'./src/**/*.{html,js}\',\n      \'node_modules/preline/dist/*.js\',\n  ],\n  plugins: [\n    // require(\'@tailwindcss/forms\'),\n      require(\'preline/plugin\'),\n  ],\n}</pre></li><li><h3>Afegiu el JavaScript de la interfície d\'usuari de Preline</h3></li><li>Incloeu el JavaScript &lt;script&gt;que impulsa els elements interactius al final de l\' &lt;/body&gt;etiqueta:</li><li><pre>// index.html\n&lt;script src=\"./node_modules/preline/dist/preline.js\"&gt;&lt;/script&gt;</pre></li></ol><p>&nbsp;</p>', 'preline.jpg', 'https://preline.co/docs/index.html', NULL, NULL, 'https://www.youtube.com/watch?v=wv9zWCm6Ui4', '2025-01-19 21:02:42', '2025-01-20 04:27:46'),
(609, 'Crear Migracions a partir de la Base de Dades', 17, 'Si has treballat en un projecte Laravel sense utilitzar migracions i ara vols buidar la base de dades mentre deixes les migracions correctes en el projecte, segueix aquests passos:', '<p>&nbsp;</p><h3>1️⃣ <strong>Crear Migracions a partir de la Base de Dades</strong></h3><p>Com que has estat treballant sense migracions, primer has de generar-les per reflectir l\'estructura actual de la base de dades. Pots utilitzar <strong>Laravel Schema Dump</strong> o una eina de tercers com <strong>Laravel Migrations Generator</strong>.</p><p>Opció 1: Utilitzant Laravel Schema Dump (Recomanada per a bases grans)</p><pre>php artisan schema:dump</pre><p>Això generarà un arxiu schema.sql dins de database/schema/, que serà aplicat quan s\'executi migrate.</p><p>Opció 2: Generar Migracions per a Cada Taula</p><p>Si vols tenir migracions separades per a cada taula, pots utilitzar:</p><pre>composer require --dev \"kitloong/laravel-migrations-generator\"\nphp artisan migrate:generate</pre><p>Aquest paquet generarà totes les migracions basades en la teva base de dades.</p><h3>2️⃣ <strong>Eliminar les Taules i les Dades</strong></h3><p>Un cop tens les migracions creades, pots buidar la base de dades de manera segura.</p><pre>php artisan db:wipe</pre><p>Això eliminarà totes les taules i després podràs aplicar les migracions.</p><h3>3️⃣ <strong>Aplicar les Migracions i les Dades Falses</strong></h3><p>Després d\'haver netejat la base de dades, pots tornar a aplicar les migracions i, si ho necessites, afegir dades de prova amb seeders:</p><pre>php artisan migrate --seed</pre><p>Si només vols migracions sense dades de prova:</p><pre>php artisan migrate</pre><h3>4️⃣ <strong>Comprovar que Tot Funciona</strong></h3><p>Finalment, assegura\'t que el projecte funciona correctament i que la base de dades té l\'estructura adequada.</p><h3><strong>Conclusió</strong></h3><p>Aquests passos et permetran: ✔ Crear migracions correctes a partir de la base de dades existent.<br>✔ Esborrar totes les taules i dades de la base de dades.<br>✔ Restaurar l\'estructura de la base de dades mitjançant migracions.<br>✔ Aplicar seeders si necessites dades de prova.</p><p>Si necessites més ajuda amb Laravel o bases de dades, fes-m\'ho saber! 🚀</p><p><strong>Referències útils:</strong></p><ul><li><a href=\"https://github.com/kitloong/laravel-migrations-generator\">Laravel Migrations Generator</a></li><li><a href=\"https://laravel.com/docs/migrations#generating-a-database-schema-dump\">Laravel Schema Dump</a></li></ul><p>&nbsp;</p>', 'migration-generator.png', 'https://github.com/kitloong/laravel-migrations-generator', NULL, NULL, 'https://www.youtube.com/watch?v=P_lD5MlkQkU', '2025-01-20 10:14:49', '2025-01-20 10:18:41'),
(610, 'optimitzar imatges en php', 1, 'optimitzar imatges en php', '<pre>&lt;?php\nfunction optimitzarImatge($fitxer, $qualitat = 85) {\n    // Comprova el tipus d\'imatge\n    $info = getimagesize($fitxer);\n    if (!$info) return false;\n\n    // Carrega la imatge segons el format\n    switch ($info[\'mime\']) {\n        case \'image/jpeg\':\n            $image = imagecreatefromjpeg($fitxer);\n            imagejpeg($image, $fitxer, $qualitat);\n            break;\n        case \'image/png\':\n            $image = imagecreatefrompng($fitxer);\n            imagepng($image, $fitxer, 9);\n            break;\n        case \'image/webp\':\n            $image = imagecreatefromwebp($fitxer);\n            imagewebp($image, $fitxer, $qualitat);\n            break;\n        default:\n            return false; // Format no suportat\n    }\n\n    imagedestroy($image);\n    return true;\n}\n\n//  Carpeta on tens les imatges\n$carpeta = \"img\"; \n\n//  Processa totes les imatges de la carpeta\n$files = scandir($carpeta);\nforeach ($files as $file) {\n    $extensio = strtolower(pathinfo($file, PATHINFO_EXTENSION));\n    \n    // Filtra només JPG, PNG i WEBP\n    if (in_array($extensio, [\'jpg\', \'jpeg\', \'png\', \'webp\'])) {\n        $ruta = \"$carpeta/$file\";\n        \n        if (optimitzarImatge($ruta)) {\n            echo \"Optimitzada: $file &lt;br&gt;\";\n        } else {\n            echo \"Error amb: $file &lt;br&gt;\";\n        }\n    }\n}\n\necho \"Procés completat!\";\n?&gt;</pre><p><br></p>', 'optimitzarImg.jpg', NULL, NULL, NULL, NULL, '2025-01-21 05:35:30', '2025-01-21 05:48:49'),
(611, 'Eliminar una carpeta de un repositori de github. ', 4, 'Eliminar una carpeta de un repositori de github. ', '<p>&nbsp; &nbsp;</p><h2>E<strong>liminar una carpeta de un repositori de github.</strong>&nbsp;</h2><h2><strong>Opció 1: utilitzant la interfície web de GitHub&nbsp;</strong></h2><p>Si la carpeta només conté arxius i no altres carpetes, podeu eliminar-la directament des del web:</p><ol><li>Ves al dipòsit a GitHub.</li><li>Navega fins a la carpeta que vols eliminar.</li><li>Obre cada fitxer dins de la carpeta i fes clic a l\'ícono de <strong>paperera</strong> (🗑️) o selecciona \"Editar\" i després \"Eliminar\".</li><li>Confirmeu els canvis escrivint un missatge de commit i fent clic a \"Commit changes\".</li><li>Una vegada que elimineu tots els fitxers de la carpeta, la carpeta desapareixerà automàticament.</li></ol><p><em>Nota:</em> GitHub no permet eliminar les carpetes buides des de la interfície web.</p><h2><strong>Opció 2: Usant Git en la terminal (Método recomanat)</strong></h2><p>Si voleu eliminar una carpeta completa (incloent el seu contingut) des del terminal, segueix aquests passos:</p><h3>1. Clonar el repositori (si encara no ho tés)</h3><pre>git clone https://github.com/usuario/repo.git\n<br></pre><h3>2. Eliminar la carpeta localment</h3><pre>git rm -r img</pre><h3>3. Confirmar l\'eliminació amb un compromís</h3><pre>git commit -m \"Eliminando la carpeta nombre_de_la_carpeta\"</pre><h3>4. Subir els canvis al repositori remot</h3><pre>git push origin main  # o la rama en la que estés trabajando</pre><h2><strong>Opció 3: utilitzant GitHub Desktop</strong></h2><p>Si prefereix una eina visual:</p><ol><li>Obre <strong>GitHub Desktop</strong> i selecciona el teu dipòsit.</li><li>Feu clic a \"Obrir en Explorador de Archivos\" o \"Obrir en Finder\" (Mac).</li><li>Borra la carpeta manualment.</li><li>Voleu a <strong>GitHub Desktop</strong> i veureu els canvis detectats.</li><li>Escriu un missatge de commit i fes clic a <strong>\"Commit to main\"</strong> .</li><li>A continuació, feu <strong>Push</strong> per enviar els canvis a GitHub.</li></ol><h3>🚀 <strong>Conclusió</strong></h3><ul><li><strong>Per eliminar des de la web</strong> , heu d\'eliminar els fitxers dins de la carpeta.</li><li><strong>Per eliminar amb Git</strong> , usa git rm -r nombre_de_la_carpeta, haz un commit i un push.</li><li><strong>Si utilitzeu GitHub Desktop</strong> , borra la carpeta i subscien els canvis.</li></ul><p>&nbsp;</p>', 'deletefileGithub.jpg', 'https://docs.github.com/en/repositories/creating-and-managing-repositories/deleting-a-repository', NULL, NULL, 'https://www.youtube.com/watch?v=1lN_ErgbQmM', '2025-01-21 17:24:10', '2025-01-21 17:24:10'),
(612, 'formes de barrejar php i html', 4, ' La primera forma (amb echo) → \"PHP Embedded in Strings\" / La segona forma (obrint i tancant PHP) → \"PHP Alternating Syntax\"', '<p>&nbsp;</p><p>Les dues formes de fer-ho tenen noms específics en PHP i desenvolupament web:</p><h2><strong>1️⃣ La primera forma (amb echo) → \"PHP Embedded in Strings\"</strong></h2><p>Quan utilitzes echo per imprimir tot el codi HTML dins de PHP, s\'anomena <strong>\"PHP Embedded in Strings\"</strong> o simplement <strong>\"Concatenació d\'strings en PHP\"</strong>.</p><h3>🔹 Exemple:</h3><pre>echo \'&lt;div class=\"card\"&gt;\';\r\necho \'   &lt;h3&gt;\' . $title . \'&lt;/h3&gt;\';\r\necho \'   &lt;p&gt;\' . $content . \'&lt;/p&gt;\';\r\necho \'&lt;/div&gt;\';</pre><p>🔴 <strong>Desavantatges:</strong></p><ul><li>Difícil de llegir.</li><li>Pots perdre cometes o tancar malament els div.</li><li>Més propens a errors quan el codi creix.</li></ul><p>🟢 <strong>Quan usar-ho?</strong></p><ul><li>Quan només vols imprimir una línia curta i simple.</li></ul><h2><strong>2️⃣ La segona forma (obrint i tancant PHP) → \"PHP Alternating Syntax\"</strong></h2><p>Aquesta és la <strong>manera recomanada</strong> quan barreges PHP amb HTML. En anglès, se li diu <strong>\"PHP Alternating Syntax\"</strong> o <strong>\"Short Open Tags in HTML\"</strong>.</p><p>📌 <strong>Útil quan tens blocs de codi HTML dins de PHP.</strong></p><h3>🔹 Exemple:</h3><pre>&lt;?php if ($result): ?&gt;\r\n    &lt;div class=\"card\"&gt;\r\n        &lt;h3&gt;&lt;?= $title ?&gt;&lt;/h3&gt;\r\n        &lt;p&gt;&lt;?= $content ?&gt;&lt;/p&gt;\r\n    &lt;/div&gt;\r\n&lt;?php endif; ?&gt;</pre><p>🟢 <strong>Avantatges:</strong></p><ul><li><strong>Molt més llegible!</strong></li><li><strong>No cal concatenar strings</strong> (.).</li><li><strong>Evita errors</strong> perquè només escrius HTML dins de PHP.</li><li><strong>Codi més net</strong> i fàcil de mantenir.</li></ul><p>🔴 <strong>Quan NO usar-ho?</strong></p><ul><li>Si tot és codi PHP pur (sense HTML), aleshores millor fer echo normal.</li></ul><h2><strong>3️⃣ La forma amb &lt;?= (short echo tag) → \"Short Echo Syntax\"</strong></h2><p>Quan vols imprimir variables a HTML de forma ràpida, en lloc de echo, pots usar &lt;?= (equivalent a echo).</p><h3>🔹 Exemple:</h3><pre>&lt;p&gt;Benvingut, &lt;?= $username ?&gt;!&lt;/p&gt;</pre><p>Equival a:</p><pre>&lt;p&gt;Benvingut, &lt;?php echo $username; ?&gt;!&lt;/p&gt;</pre><p>🟢 <strong>Avantatges:</strong></p><ul><li>És més curt i ràpid.</li><li>Funciona perfectament dins de blocs HTML.</li></ul><p>🔴 <strong>Quan NO usar-ho?</strong></p><ul><li>Si la versió de PHP està mal configurada (short_open_tag=Off, però això ja no és problema a PHP 5.4+).</li></ul><h2><strong>🎯 Quina és la millor?</strong></h2><p>✅ <strong>Per projectes grans amb molt HTML, usa</strong> PHP Alternating Syntax (obrint i tancant PHP).<br>✅ <strong>Per imprimir variables ràpidament, usa &lt;?= (Short Echo Syntax).</strong><br>✅ <strong>Per una línia curta de text dins de PHP, echo està bé.</strong></p><p>🔹 <strong>La que t’he donat abans és la més neta i fàcil de mantenir!</strong> 🚀💡</p><p>📌 <strong>Resum ràpid:</strong></p><p><strong>MètodeNom tècnicAvantatgesQuan fer-lo servir?</strong>echo amb concatenació | <strong>\"PHP Embedded in Strings\"</strong> | Fàcil per una línia curta | Quan imprimeixes una cosa ràpida<br>Obrir i tancar PHP | <strong>\"PHP Alternating Syntax\"</strong> | Llegible, fàcil de mantenir | Quan HTML és més llarg<br>&lt;?= (short echo) | <strong>\"Short Echo Syntax\"</strong> | Ràpid per imprimir variables | Quan només necessites mostrar dades simples</p><p>Espero que això et sigui útil! <strong>Ara ja saps com organitzar millor el teu codi en PHP!</strong> 😃🚀&nbsp;</p>', 'phpHtml.jpg', NULL, NULL, NULL, NULL, '2025-01-22 18:07:46', '2025-01-22 18:09:34'),
(613, 'sql expert', 4, 'ChatGPT SQL Expert és una eina avançada per optimitzar bases de dades, escriure consultes SQL eficients i resoldre problemes de rendiment. Ideal per a desenvolupadors, DBA i DevOps que busquen solucions ràpides i precises. 🚀', '<div>&nbsp;</div><div><strong>ChatGPT SQL Expert: L’eina definitiva per a optimitzar bases de dades<br></strong><br></div><div><br>💡 <strong>ChatGPT SQL Expert</strong> és una eina avançada per als professionals de bases de dades, desenvolupadors i administradors de sistemes que necessiten <strong>crear consultes SQL eficients</strong>, optimitzar estructures i solucionar problemes de rendiment.<br><br></div><div><strong><br>🔹 Funcionalitats principals<br></strong><br></div><div><br>✔️ <strong>Escriure consultes SQL avançades</strong>: Des de JOIN complexos fins a subconsultes optimitzades.<br>✔️ <strong>Optimització de bases de dades</strong>: Recomanacions per a millorar indexes, views i stored procedures.<br>✔️ <strong>Depuració i anàlisi de consultes</strong>: Identificació de colls d’ampolla i suggeriments per millorar el rendiment.<br>✔️ <strong>Suport a múltiples motors de base de dades</strong>: MySQL, MariaDB, PostgreSQL, SQL Server, Oracle, SQLite, i més.<br><br></div><div><strong><br>🚀 Per què utilitzar ChatGPT SQL Expert?<br></strong><br></div><div><br>🔹 <strong>Estalvia temps</strong>: Respon preguntes en segons, sense necessitat de buscar documentació.<br>🔹 <strong>Millora el rendiment</strong>: Detecta consultes lentes i suggereix optimitzacions.<br>🔹 <strong>Seguretat i bones pràctiques</strong>: Ajuda a prevenir atacs SQL Injection i altres vulnerabilitats.<br><br></div><div><strong><br>💻 Aplicacions pràctiques<br></strong><br></div><ul><li>Desenvolupadors backend que treballen amb Laravel, Symfony, Django, etc.</li><li>Administradors de bases de dades que volen optimitzar el rendiment.</li><li>Enginyers DevOps que gestionen bases de dades escalables.</li></ul><div><br>💬 <strong>Necessites ajuda amb SQL?</strong> Prova <strong>ChatGPT SQL Expert</strong> i porta les teves consultes al següent nivell! 🚀🔥&nbsp;<br><br></div>', 'sql_expert.png', 'https://chatgpt.com/g/g-m5lMeGifF-sql-expert/c/679133b5-3bfc-8013-8329-3ab0fa865a4a', '', '', '', '2025-01-23 05:53:09', '2025-01-23 05:53:09');
INSERT INTO `posts` (`id`, `title`, `category_id`, `excerpt`, `content`, `img`, `url`, `ins`, `face`, `youtube`, `created_at`, `updated_at`) VALUES
(614, 'localhost', 17, 'Localhost és l\'adreça utilitzada per a provar aplicacions web de manera local, sense necessitat d\'un servidor extern. És essencial per a desenvolupadors que treballen amb PHP, MySQL i altres tecnologies backend.', '<div>&nbsp;</div><div><strong>Què és Localhost i per què és imprescindible per a desenvolupadors?<br></strong><br></div><div><br>💡 <strong>Localhost (127.0.0.1)</strong> és una adreça IP especial que es refereix a la pròpia màquina de l’usuari. Quan un desenvolupador vol provar una aplicació web sense publicar-la en línia, <strong>localhost</strong> permet executar-la en un entorn segur i local.<br><br></div><div><strong><br>🔹 Per què utilitzar localhost?<br></strong><br></div><div><br>✔️ <strong>Provar aplicacions sense internet</strong> – No cal cap connexió externa.<br>✔️ <strong>Desenvolupament segur</strong> – Pots fer proves sense afectar usuaris reals.<br>✔️ <strong>Velocitat de càrrega ràpida</strong> – Sense latència ni dependència de servidors externs.<br>✔️ <strong>Integració amb XAMPP, MAMP, WAMP</strong> – Serveis locals per executar <strong>PHP, MySQL, Apache</strong> i més.<br><br></div><div><strong><br>🚀 Com configurar localhost?<br></strong><br></div><div><br>🛠️ Per executar <strong>localhost</strong> al teu ordinador, pots fer servir eines com:<br><br></div><ul><li><strong>XAMPP</strong> – Ideal per PHP, MariaDB i Apache.</li><li><strong>WAMP/MAMP</strong> – Alternatives per Windows i macOS.</li><li><strong>Node.js amb Express</strong> – Per executar servidors locals en JavaScript.</li></ul><div><strong><br>🔗 Exemples d\'ús<br></strong><br></div><div><br>📌 <strong>Accés bàsic</strong> → Obrir un navegador i escriure http://localhost/<br>📌 <strong>Gestionar bases de dades</strong> → http://localhost/phpmyadmin/<br>📌 <strong>Executar una API local</strong> → http://localhost:3000/ amb Node.js<br><br></div><div><strong><br>💡 Conclusió<br></strong><br></div><div><br>🚀 <strong>Localhost és una eina imprescindible per a qualsevol desenvolupador!</strong> Permet desenvolupar, provar i optimitzar aplicacions web sense haver de dependre d’un servidor remot.<br><br></div><div><br>🔹 <strong>Utilitzes localhost en els teus projectes?</strong> Explica la teva experiència! 😃🔥&nbsp;<br><br></div>', 'localhost.png', 'http://localhost', '', '', '', '2025-01-23 08:30:47', '2025-01-23 08:30:47'),
(633, 'tailwind 5 filtres i paginacio a postslar11', 1, 'tailwind 5 filtres i paginacio a postslar11', '<pre>&nbsp; &lt;?php<br>&nbsp; // Configuració de la connexió a la base de dades<br>&nbsp; $servername = \"localhost\";<br>&nbsp; $username = \"joan\";<br>&nbsp; $password = \"queMm88/g62123\";<br>&nbsp; $dbname = \"postslar11\";<br><br>&nbsp; // Crear connexió segura<br>&nbsp; $conn = new mysqli($servername, $username, $password, $dbname);<br>&nbsp; if ($conn-&gt;connect_error) {<br>&nbsp; &nbsp; die(\"Connexió fallida: \" . $conn-&gt;connect_error);<br>&nbsp; }<br><br>&nbsp; // Obtenir paràmetres de la URL<br>&nbsp; $action = $_GET[\'action\'] ?? \'index\';<br>&nbsp; $categoryId = $_GET[\'category_id\'] ?? null;<br>&nbsp; $tagId = $_GET[\'tag_id\'] ?? null;<br>&nbsp; $query = $_GET[\'query\'] ?? null;<br><br>&nbsp; // Variables de paginació<br>&nbsp; $postsPerPage = 15;<br>&nbsp; $currentPage = isset($_GET[\'page\']) ? max(1, intval($_GET[\'page\'])) : 1;<br>&nbsp; $offset = ($currentPage - 1) * $postsPerPage;<br><br>&nbsp; // Funció per obtenir categories<br>&nbsp; function getCategories($conn)<br>&nbsp; {<br>&nbsp; &nbsp; $result = $conn-&gt;query(\"SELECT * FROM categories ORDER BY name ASC\");<br>&nbsp; &nbsp; return $result-&gt;fetch_all(MYSQLI_ASSOC);<br>&nbsp; }<br><br>&nbsp; // Funció per obtenir etiquetes<br>&nbsp; function getEtiquetas($conn)<br>&nbsp; {<br>&nbsp; &nbsp; $result = $conn-&gt;query(\"SELECT * FROM etiquetas ORDER BY name ASC\");<br>&nbsp; &nbsp; return $result-&gt;fetch_all(MYSQLI_ASSOC);<br>&nbsp; }<br><br>&nbsp; // Funció per obtenir posts amb filtres i paginació<br>&nbsp; function getPosts($conn, $filter = null, $search = null, $tag = null, $limit, $offset)<br>&nbsp; {<br>&nbsp; &nbsp; $sql = \"SELECT SQL_CALC_FOUND_ROWS p.*, c.name AS category_name, p.url, p.ins, p.face, p.youtube, p.updated_at&nbsp;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM posts p&nbsp;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; LEFT JOIN categories c ON p.category_id = c.id\";<br><br>&nbsp; &nbsp; if ($filter) {<br>&nbsp; &nbsp; &nbsp; $sql .= \" WHERE p.category_id = \" . intval($filter);<br>&nbsp; &nbsp; }<br><br>&nbsp; &nbsp; if ($search) {<br>&nbsp; &nbsp; &nbsp; $sql .= $filter ? \" AND\" : \" WHERE\";<br>&nbsp; &nbsp; &nbsp; $sql .= \" (p.title LIKE \'%\" . $conn-&gt;real_escape_string($search) . \"%\'&nbsp;<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; OR c.name LIKE \'%\" . $conn-&gt;real_escape_string($search) . \"%\')\";<br>&nbsp; &nbsp; }<br><br>&nbsp; &nbsp; if ($tag) {<br>&nbsp; &nbsp; &nbsp; $sql = \"SELECT SQL_CALC_FOUND_ROWS p.*, p.url, p.ins, p.face, p.youtube FROM posts p<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; INNER JOIN post_etiquetas pe ON p.id = pe.post_id<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE pe.etiqueta_id = \" . intval($tag);<br>&nbsp; &nbsp; }<br><br>&nbsp; &nbsp; $sql .= \" ORDER BY p.updated_at DESC LIMIT $limit OFFSET $offset\";<br>&nbsp; &nbsp; $result = $conn-&gt;query($sql);<br><br>&nbsp; &nbsp; $totalPosts = $conn-&gt;query(\"SELECT FOUND_ROWS() as total\")-&gt;fetch_assoc()[\'total\'];<br><br>&nbsp; &nbsp; return [\'posts\' =&gt; $result-&gt;fetch_all(MYSQLI_ASSOC), \'total\' =&gt; $totalPosts];<br>&nbsp; }<br><br>&nbsp; // Gestionar accions<br>&nbsp; $categories = getCategories($conn);<br>&nbsp; $etiquetas = getEtiquetas($conn);<br>&nbsp; $posts = [];<br>&nbsp; $totalPosts = 0;<br><br>&nbsp; $result = getPosts($conn, $categoryId, $query, $tagId, $postsPerPage, $offset);<br>&nbsp; $posts = $result[\'posts\'] ?? [];<br>&nbsp; $totalPosts = $result[\'total\'] ?? 0;<br>&nbsp; $totalPages = ceil($totalPosts / $postsPerPage);<br>&nbsp; ?&gt;<br><br><br><br>&nbsp; &lt;!doctype html&gt;<br>&nbsp; &lt;html&gt;<br><br>&nbsp; &lt;head&gt;<br>&nbsp; &nbsp; &lt;meta charset=\"UTF-8\"&gt;<br>&nbsp; &nbsp; &lt;meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\"&gt;<br>&nbsp; &nbsp; &lt;script src=\"https://unpkg.com/@tailwindcss/browser@4\"&gt;&lt;/script&gt;<br>&nbsp; &nbsp; &lt;!--Regular Datatables CSS--&gt;<br>&nbsp; &nbsp; &lt;link href=\"https://cdn.datatables.net/1.10.19/css/jquery.dataTables.min.css\" rel=\"stylesheet\"&gt;<br>&nbsp; &nbsp; &lt;!--Responsive Extension Datatables CSS--&gt;<br>&nbsp; &nbsp; &lt;link href=\"https://cdn.datatables.net/responsive/2.2.3/css/responsive.dataTables.min.css\" rel=\"stylesheet\"&gt;<br>&nbsp; &nbsp; &lt;link href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css\" rel=\"stylesheet\"&gt;<br>&nbsp; &nbsp; &lt;!-- FANCYBOX --&gt;<br>&nbsp; &nbsp; &lt;link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/@fancyapps/ui@4.0/dist/fancybox.css\" /&gt;<br>&nbsp; &nbsp; &lt;script src=\"https://cdn.jsdelivr.net/npm/@fancyapps/ui@4.0/dist/fancybox.umd.js\"&gt;&lt;/script&gt;<br>&nbsp; &nbsp; &lt;style&gt;<br>&nbsp; &nbsp; &nbsp; /*Overrides for Tailwind CSS */<br><br>&nbsp; &nbsp; &nbsp; /*Form fields*/<br>&nbsp; &nbsp; &nbsp; .dataTables_wrapper select,<br>&nbsp; &nbsp; &nbsp; .dataTables_wrapper .dataTables_filter input {<br>&nbsp; &nbsp; &nbsp; &nbsp; color: #4a5568;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*text-gray-700*/<br>&nbsp; &nbsp; &nbsp; &nbsp; padding-left: 1rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*pl-4*/<br>&nbsp; &nbsp; &nbsp; &nbsp; padding-right: 1rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*pl-4*/<br>&nbsp; &nbsp; &nbsp; &nbsp; padding-top: .5rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*pl-2*/<br>&nbsp; &nbsp; &nbsp; &nbsp; padding-bottom: .5rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*pl-2*/<br>&nbsp; &nbsp; &nbsp; &nbsp; line-height: 1.25;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*leading-tight*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border-width: 2px;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*border-2*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border-radius: .25rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; border-color: #edf2f7;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*border-gray-200*/<br>&nbsp; &nbsp; &nbsp; &nbsp; background-color: #edf2f7;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*bg-gray-200*/<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; /*Row Hover*/<br>&nbsp; &nbsp; &nbsp; table.dataTable.hover tbody tr:hover,<br>&nbsp; &nbsp; &nbsp; table.dataTable.display tbody tr:hover {<br>&nbsp; &nbsp; &nbsp; &nbsp; background-color: #ebf4ff;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*bg-indigo-100*/<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; /*Pagination Buttons*/<br>&nbsp; &nbsp; &nbsp; .dataTables_wrapper .dataTables_paginate .paginate_button {<br>&nbsp; &nbsp; &nbsp; &nbsp; font-weight: 700;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*font-bold*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border-radius: .25rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*rounded*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border: 1px solid transparent;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*border border-transparent*/<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; /*Pagination Buttons - Current selected */<br>&nbsp; &nbsp; &nbsp; .dataTables_wrapper .dataTables_paginate .paginate_button.current {<br>&nbsp; &nbsp; &nbsp; &nbsp; color: #fff !important;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*text-white*/<br>&nbsp; &nbsp; &nbsp; &nbsp; box-shadow: 0 1px 3px 0 rgba(0, 0, 0, .1), 0 1px 2px 0 rgba(0, 0, 0, .06);<br>&nbsp; &nbsp; &nbsp; &nbsp; /*shadow*/<br>&nbsp; &nbsp; &nbsp; &nbsp; font-weight: 700;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*font-bold*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border-radius: .25rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*rounded*/<br>&nbsp; &nbsp; &nbsp; &nbsp; background: #667eea !important;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*bg-indigo-500*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border: 1px solid transparent;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*border border-transparent*/<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; /*Pagination Buttons - Hover */<br>&nbsp; &nbsp; &nbsp; .dataTables_wrapper .dataTables_paginate .paginate_button:hover {<br>&nbsp; &nbsp; &nbsp; &nbsp; color: #fff !important;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*text-white*/<br>&nbsp; &nbsp; &nbsp; &nbsp; box-shadow: 0 1px 3px 0 rgba(0, 0, 0, .1), 0 1px 2px 0 rgba(0, 0, 0, .06);<br>&nbsp; &nbsp; &nbsp; &nbsp; /*shadow*/<br>&nbsp; &nbsp; &nbsp; &nbsp; font-weight: 700;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*font-bold*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border-radius: .25rem;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*rounded*/<br>&nbsp; &nbsp; &nbsp; &nbsp; background: #667eea !important;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*bg-indigo-500*/<br>&nbsp; &nbsp; &nbsp; &nbsp; border: 1px solid transparent;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*border border-transparent*/<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; /*Add padding to bottom border */<br>&nbsp; &nbsp; &nbsp; table.dataTable.no-footer {<br>&nbsp; &nbsp; &nbsp; &nbsp; border-bottom: 1px solid #e2e8f0;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*border-b-1 border-gray-300*/<br>&nbsp; &nbsp; &nbsp; &nbsp; margin-top: 0.75em;<br>&nbsp; &nbsp; &nbsp; &nbsp; margin-bottom: 0.75em;<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; /*Change colour of responsive icon*/<br>&nbsp; &nbsp; &nbsp; table.dataTable.dtr-inline.collapsed&gt;tbody&gt;tr&gt;td:first-child:before,<br>&nbsp; &nbsp; &nbsp; table.dataTable.dtr-inline.collapsed&gt;tbody&gt;tr&gt;th:first-child:before {<br>&nbsp; &nbsp; &nbsp; &nbsp; background-color: #667eea !important;<br>&nbsp; &nbsp; &nbsp; &nbsp; /*bg-indigo-500*/<br>&nbsp; &nbsp; &nbsp; }<br>&nbsp; &nbsp; &lt;/style&gt;<br><br>&nbsp; &nbsp; &lt;!-- Masonry --&gt;<br>&nbsp; &nbsp; &lt;style&gt;<br>&nbsp; &nbsp; &nbsp; .masonry {<br>&nbsp; &nbsp; &nbsp; &nbsp; column-count: 4;<br>&nbsp; &nbsp; &nbsp; &nbsp; column-gap: 1rem;<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; .masonry-item {<br>&nbsp; &nbsp; &nbsp; &nbsp; break-inside: avoid;<br>&nbsp; &nbsp; &nbsp; &nbsp; margin-bottom: 1rem;<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp; &nbsp; &nbsp; @media (max-width: 1200px) {<br>&nbsp; &nbsp; &nbsp; &nbsp; .masonry {<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; column-count: 3;<br>&nbsp; &nbsp; &nbsp; &nbsp; }<br>&nbsp; &nbsp; &nbsp; }<br><br>&nbsp;